public class Model {
    private boolean isGameEnd;
    private int score;
    // Game Objects
    private PlayerObject player;
    private PlayerObject p2 = null; // Player 2
    private CopyOnWriteArrayList<EnemyObject> EnemiesList;
    private CopyOnWriteArrayList<BulletObject> BulletList;
    // Enemy Bullet list
    private CopyOnWriteArrayList<BulletObject> eBulletList;
    // Pickup objects
    private CopyOnWriteArrayList<PickupObject> pickupList;
    // Effect and explosion List
    private CopyOnWriteArrayList<EffectObject> effectList;
    // Game logic class
    private GameLogic gameLogic;
    // Score UI Numbers
    public GameResource[] numbers = new GameResource[]{
            new GameResource("res/numbers_910_152(91).png", 91, 152, 0),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 91),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 182),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 273),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 364),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 455),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 546),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 637),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 728),
            new GameResource("res/numbers_910_152(91).png", 91, 152, 819),
    };
    public GameResource shieldResource;
    // Game Resources
    public GameResource playerResource;
    // Enemies
    public GameResource bulletResource;
    // Background
    public GameResource backgroundResource;
    public GameResource bgEffectResource;
    // Player explosion
    public GameResource explosionRes;
    public GameResource explosionEnemy;
    public GameResource p2Res;
    public GameResource bulletEf;
    public GameResource laserEf;

    public Model() {
        // Background
        backgroundResource = new GameResource("res/BG_1080_3838.png", 3838, 1080, "sound/space.wav");
        bgEffectResource = new GameResource("res/BG_Effect_1000.png", 1000, 2000);
        //Player
        playerResource = new GameResource("res/player_750_160.png", 125, 160, "sound/player_explosion.wav");
        // Bullets
        bulletResource = new GameResource("res/Player_Bullet_76_18(18-15-10).png", 10, 18);
        // Laser has the same centre as player
        shieldResource = new GameResource("res/shield2.png", 135, 133);
        explosionRes = new GameResource("res/exp_player.png", 64,64);
        explosionEnemy = new GameResource("res/explosion_enemy.png", 32,32, "sound/explosion_enemy.wav");
        p2Res = new GameResource("res/ship2.png", 125, 140, "sound/player_explosion.wav");
        bulletEf = new GameResource("res/bullet_effect_16_16.png", 16, 16, "sound/bullet.wav");
        laserEf = new GameResource("res/laser_effect_16_16.png", 16, 16, "sound/laser.wav");
    }
    // Setup game world, or The method reset the whole game
    void resetWorld() {
        backgroundResource.loopSound();
        isGameEnd = false;
        score = 0;
        player = new PlayerObject(playerResource, 50, 50, new Point3f(GlobalConst.LAYOUT_WIDTH/2f, GlobalConst.LAYOUT_HEIGHT/2f, 0));
        EnemiesList = new CopyOnWriteArrayList<>();
        BulletList = new CopyOnWriteArrayList<>();
        eBulletList = new CopyOnWriteArrayList<>();
        pickupList = new CopyOnWriteArrayList<>();
        effectList = new CopyOnWriteArrayList<>();
        gameLogic = new GameLogic(this);
    }
    // Getter Setter
    public PlayerObject getPlayer() {
        return player;
    }
    public CopyOnWriteArrayList<EnemyObject> getEnemies() {
        return EnemiesList;
    }
    public CopyOnWriteArrayList<BulletObject> getBullets() {
        return BulletList;
    }
    public CopyOnWriteArrayList<BulletObject> getEBulletList() {
        return eBulletList;
    }
    public CopyOnWriteArrayList<PickupObject> getPickupList() {
        return pickupList;
    }
    public CopyOnWriteArrayList<EffectObject> getEffectList() {
        return effectList;
    }
    // add score for enemy object
    void addScore(EnemyObject object) {
        score += object.getScore();
    }
    void addScore() {
        score++;
    }
    int getScore() {return score;}
    public boolean isGameEnd() {
        return isGameEnd;
    }
    public void setGameEnd() {
        backgroundResource.stopSound();
        isGameEnd = true;
        player = null;
        p2 = null;
    }
    public GameLogic getGameLogic() {
        return gameLogic;
    }
    public PlayerObject getP2() {
        return p2;
    }
    public void setP2() {
        this.p2 = new PlayerObject(p2Res, 50, 56, new Point3f(GlobalConst.LAYOUT_WIDTH/3f, GlobalConst.LAYOUT_HEIGHT/2f, 0));
    }
}


